import { USER_URL } from "../constant";
import { apiSlice } from "./apiSlice";
export const userApiSlice = apiSlice.injectEndpoints({
    endpoints: (builder) => ({
        login:builder.mutation({
            query:(data)=>({
                url:`USER_URL/auth`,
                method : 'POST',
                body :data,
            }),

            providesTags :['Users']
        }),

    })
})

export const {useLoginMutation} = userApiSlice;
