import { createSlice } from "@reduxjs/toolkit";


const initialState =localStorage.getItem('cart') ? JSON.parse(localStorage.getItem('cart')):
{cartItems:[]};

export const addDecimals =(num)=>{
    return (Math.round(num*100)/100).toFixed(2);
}

const cartSlice = createSlice({
    name:'cart',
    initialState,
    reducers:{
        //state is current state of the cart and action is data in the payload
        addToCart:(state,action)=>{
            const item = action.payload;
            const existItem = state.cartItems.find((x)=> x._id ===item._id);//to check if the item exists in the cart or not

            //if item exist then we will add that item to existing items
            if(existItem){
                state.cartItems = state.cartItems.map((x)=>
                    x._id == existItem._id ?item:x
                )

            }

            //if item doesnt exist then we will add that item to cart
            else{
                state.cartItems = [...state.cartItems,item];
            }

            //calculate the item price
            state.itemPrice= addDecimals(
                state.cartItems.reduce((acc,item)=>
                {

               console.log(item.price);
               console.log(acc);
               return acc+item.price*item.qty,0;
                }



        ))
        console.log(state.itemPrice);




            //calculate the shipping price
            //if order is > 100 then free else 10$
            state.shippingPrice = addDecimals(state.itemPrice>100 ? 0 :10);



            //calculate the tax price
            //tax price will be the 15% of the item price
            state.taxPrice = addDecimals(Number(0.15*state.itemPrice).toFixed(2));

            //calculate the total price
            state.totalprice =(
                Number(state.itemPrice)+
                Number(state.shippingPrice)+
                Number(state.taxPrice)

            ).toFixed(2);


                //now save this all the data of the cart and price in local storages
                localStorage.setItem('cart',JSON.stringify(state));
        }
    },
})

export const {addToCart} = cartSlice.actions;

export default cartSlice.reducer;
